mapper.select:
SELECT count(1) as amount, if(sum(a.area) is null,0,sum(a.area)) as area,if(sum(b.worker_num) is null,0,sum(b.worker_num)) as worker_num
FROM t_product a
inner join
t_planting_industry b on a.obj_id = b.id


        and a.town = #{town}


        and a.village = #{village}

SELECT count(1) as amount,if(sum(a.area) is null,0,sum(a.area)) as area,if(sum(b.worker_num) is null,0,sum(b.worker_num)) as worker_num
FROM t_product a
         inner join t_cultivation b on a.obj_id = b.id



        and a.town = #{town}


        and a.village = #{village}

select count(1) as amount,if(sum(b.area) is null,0,sum(b.area)) as area, if(sum(ylut * b.area) is null,0, sum(ylut * b.area)) as yield
from t_tea b inner join t_product a on b.id=a.obj_id


        and a.town = #{town}


        and a.village = #{village}

select id, case type when 8 then '斜背茶' when 11 then '养殖场' when 12 then '果园/茶园' when 13 then '种植场' end as type, name, address, if(details is null, '', details) as details, addArr, longitude, latitude, image, if(video is null, '', video) as video, if(vRaddress is null, '', vRaddress) as vRaddress from t_product a type in (8, 11, 12, 13)
and a.town = #{town}
and a.village = #{village}
