mapper.select:
select id,case type when 10 then '规划招商资产' end as type,area,name,address,if(details is null,'',details ) as
details,addArr,longitude,latitude,image,if(video is null,'',video ) as video,if(vRaddress is null,'',vRaddress )
as vRaddress from t_product

    type=10

        and town = #{town}


        and village = #{village}

SELECT count(1) AS number, DATE_FORMAT(b.invest_time, '%Y-%m') AS yearOfMonth, DATE_FORMAT(b.invest_time, '%m') AS month,sum(a.fund_amount) AS amount FROM t_future_product a right join t_investment_history b on a.id=b.product_id left join t_product as c on a.id= c.obj_id
(a.plant='2' or a.plant='3')
and c.town = #{town}
and c.village = #{village}
group by DATE_FORMAT(b.invest_time, '%Y-%m'), DATE_FORMAT(b.invest_time, '%m') limit 10, 6

select case when type_in_future = '1' then '计划项目' when type_in_future = '2' then '拟签约项目' when type_in_future = '3' then '已签约项目' else '未明确状态项目' end as typeInFuture, count(1) as num, DATE_FORMAT(now(), '%Y') as investTime, sum(b.invest_amount) as investAmount from t_investment_history b left join t_future_product a on a.id = b.product_id left join t_product as c on a.id = c.obj_id
(a.plant = '2' or a.plant = '3') and DATE_FORMAT(b.invest_time, '%Y') = DATE_FORMAT(now(), '%Y') and type_in_future = '2'
and c.town = #{town}
and c.village = #{village}
GROUP BY type_in_future
